		dataType: 'json',
		data: {
			chartId: chartid,
			chartType: chartType,
			chartOptAllObj: JSON.stringify(chartOptAllObj),
			chartConfigPositionKeyStr: JSON.stringify(filteredchartOptAllObj),
			chartData: chartData
		},
