function formatnumber(number) {
	var unitlist = ["", "K", "M", "B", "T"];
	let sign = Math.sign(number);
	let unit = 0;
	while (Math.abs(number) > 1000) {
		unit = unit + 1;
		number = Math.floor(Math.abs(number) / 10) / 100;
	}
	return sign * Math.abs(number) + unitlist[unit];
}

